FCJob::

    collect_one_segment

( prev ) ->





        cut_dummy_nodes := empty vec;


        unit_nodes := empty vec;



        while true :




            curr := cut_contiguous_non_boundary_dummies( prev, cut_dummy_nodes );


            /* This call actually cuts dummies out of the LL, but we're not bumping curr_list_ver just yet. */



            match curr :




                case CommittedUnit :




                    unit_nodes.push( curr );



                    prev := curr;



                    continue;




                case nullptr or dummy :




                    /* If it's a dummy, then the dummy was a boundary and/or a fence, at the time it was originally traversed. */



                    break;






        return (cut_dummy_nodes, unit_nodes, curr)

